import React from 'react';

const HotelImage = ({ hotel, className = '', showId = false }) => {
  const getHotelImageUrl = (hotel) => {
    // if (hotel.images && hotel.images.length > 0 && hotel.images[0]) {
    //   return hotel.images[0];
    // }
    return `https://raw.githubusercontent.com/wso2con/2025-CMB-AI-tutorial/refs/heads/main/hotel-apis/data/images/${hotel.hotelId}.jpeg`;
  };

  const handleImageError = (e, hotel) => {
    const placeholderUrl = `https://raw.githubusercontent.com/wso2con/2025-CMB-AI-tutorial/refs/heads/main/hotel-apis/data/images/${hotel.hotelId}.jpeg`;
    if (e.target.src !== placeholderUrl) {
      e.target.src = placeholderUrl;
    } else {
      // Generic SVG placeholder
      e.target.src = 'data:image/svg+xml;base64,';
    }
  };

  return (
    <div className={`hotel-image ${className}`}>
      <img 
        src={getHotelImageUrl(hotel)}
        // alt={hotel.hotelName}
        // onError={(e) => handleImageError(e, hotel)}
        loading="lazy"
      />
      {showId && (
        <div className="hotel-image-overlay">
          <span className="hotel-id">ID: {hotel.hotelId}</span>
        </div>
      )}
    </div>
  );
};

export default HotelImage;